// main function
fun main() {
    val someNullValue: String? = null
    lateinit var someMustNotNullValue: String

    try {
        someMustNotNullValue = someNullValue!!
        println(someMustNotNullValue)
    } catch (e: Exception) {
        someMustNotNullValue = "Nilai String Null"
        println(someMustNotNullValue)
    }
}

// main function
fun main() {
    val someNullValue: String? = null
    lateinit var someMustNotNullValue: String

    try {
        someMustNotNullValue = someNullValue!!
    } catch (e: Exception) {
        someMustNotNullValue = "Nilai String Null"
    } finally {
        println(someMustNotNullValue)
    }
}

import kotlin.NumberFormatException

// main function
fun main() {
    val someStringValue: String? = null
    var someIntValue = 0

    try {
        someIntValue = someStringValue!!.toInt()
    } catch (e: NullPointerException) {
        someIntValue = 0
    } catch (e: NumberFormatException) {
        someIntValue = -1
    } finally {
        when (someIntValue) {
            0 -> println("Catch block NullPointerException terpanggil!")
            -1 -> println("Catch block NumberFormatException terpanggil!")
            else -> println(someIntValue)
        }
    }
}

class Calculator { fun add(value1: Int, value2: Int) = value1 + value2 fun add(value1: Int, value2: Int, value3: Int) = value1 + value2 + value3 fun add(value1: Double, value2: Double) = value1 + value2 fun add(value1: Float, value2: Float) = value1 + value2
fun min(value1: Int, value2: Int) = if (value1 < value2) value1 else value2 fun min(value1: Double, value2: Double) = if (value1 < value2) value1 else value2 }
fun main() { val calc = Calculator()
println(calc.add(2, 4)) println(calc.add(2.5, 2.2)) println(calc.add(6f, 7f)) println(calc.add(1, 2, 3))
println(calc.min(9, 2)) println(calc.min(17.2, 18.3)) }

